mapper.update:
update MEMBER
set MNAME = #{mname,jdbcType=VARCHAR},
  CONTENT = #{content,jdbcType=VARCHAR},
  WRITER = #{writer,jdbcType=VARCHAR},
  TITLE = #{title,jdbcType=VARCHAR}
where MNO = #{mno,jdbcType=NUMERIC}

mapper.select:
select MNO, MNAME, CONTENT, WRITER, TITLE
from MEMBER
where MNO = #{mno,jdbcType=NUMERIC}
